/**
 * checkf if a string starts with requested one
 */
if (typeof String.prototype.startsWith != 'function') {
    String.prototype.startsWith = function (str){
        return this.slice(0, str.length) == str;
    };
}

/**
 * Capitalize first letter on every word of provided string
 * Lowercase others
 */
if (typeof String.prototype.capitalize != 'function') {
	String.prototype.capitalize = function (){
		return this.toLowerCase().replace( /\b\w/g, function (m) {
			return m.toUpperCase();
		});
	};
}

/**
 * equivalent for php's explode() as javascript::split() cuts at limit
 */
if (typeof String.prototype.explode != 'function') {
    String.prototype.explode = function (separator, limit) {
        const array = this.split(separator);
        if (limit !== undefined && array.length >= limit) {
            array.push(array.splice(limit - 1).join(separator));
        }
        return array;
    };
}

/**
 * As Date.parse(string) is implementation dependend, create own YYYY-mm-dd parser
 * https://stackoverflow.com/questions/2587345/why-does-date-parse-give-incorrect-results
 * @param {string} input YYYY-mm-dd date format string
 */
function parseDate(input) {
    var parts = input.split('-');
    // new Date(year, month [, day [, hours[, minutes[, seconds[, ms]]]]])
    var d= new Date(parts[0], parts[1]-1, parts[2]); // Note: months are 0-based
    return d.getTime();
}

/**
 * Returns a random string of length 'len'
 * from: http://stackoverflow.com/questions/1349404/generate-random-string-characters-in-javascript
 * @param {number} len resulting string length
 * @return {string} resulting random string
 */
function getRandomString(len) {
	var s = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
	return new Array(len).join().split(',').map(function() { return s.charAt(Math.floor(Math.random() * s.length)); }).join('');
}

/**
* Compose session identifier for given client options
 *
 * identifier is composed as: "base:sessid:view:mode:name"
 * base: source family
 * ring: session id for conected ring
 * View: Display style
 * Mode: Display options
 * Name: unique client name. Must not contain ':' recommended example: basename_xxrandom8@ring
 * @param {string} opts pointer to ac_clientOpts structure
 * @return {string} composed string
*/
function composeClientSessionName(opts) {
    return opts.Source + ":" + opts.Ring + ":" + opts.View + ":" + opts.Mode + ":" + opts.Name;
}

