// coge un objeto, {key:val,....} y lo convierte en otro de la forma {{'Key': objKey,'Value':objValue},...}
function toKeyValue(obj) {
	var res=[];
	for ( var key in obj)  if (obj.hasOwnProperty(key)) res.push({'Key':key,'Value':obj[key]});
